authRouter.get('/retest', async (req, res, next) => {
  try {
    const user = await User.findOne({
      where: { accessToken: req.query.token, loginType: req.query.type },
    });
    res.status(200).json(user);
  } catch (e) {
    console.error(e);
    next(e);
  }
});
export default authRouter;
